/*---- CLASS MEMBERS -------------------------------------------------------------------------------------------------*/


/**-------------------------------------------------------------------------------------------------------------------
* 
* @fn         XASN1::XASN1()
* @brief      Constructor
* @ingroup    XUTILS
* 
* @return     Does not return anything. 
* 
* --------------------------------------------------------------------------------------------------------------------*/
XASN1::XASN1()
{
  Clean();
}


/**-------------------------------------------------------------------------------------------------------------------
* 
* @fn         XASN1::~XASN1()
* @brief      Destructor
* @note       VIRTUAL
* @ingroup    XUTILS
* 
* @return     Does not return anything. 
* 
* --------------------------------------------------------------------------------------------------------------------*/
XASN1::~XASN1()
{
  if(ber)
    {
      delete ber;
    }

  Clean();
}


/**-------------------------------------------------------------------------------------------------------------------
* 
* @fn         XCHAR* XASN1::GetOIDDescription(XCHAR* OID)
* @brief      GetOIDDescription
* @ingroup    XUTILS
* 
* @param[in]  OID : 
* 
* @return     XCHAR* : 
* 
* --------------------------------------------------------------------------------------------------------------------*/
XCHAR* XASN1::GetOIDDescription(XCHAR* OID)
{
  XSTRING _OID = OID;

  for(XDWORD c=0; c<(sizeof(OID_description)/sizeof(XASN1_OID_DESCRIPTION)); c++)
    {
      if(!_OID.Compare(OID_description[c].OID, true))
        {
          return OID_description[c].description;
        }
    }

  return NULL;
}


/**-------------------------------------------------------------------------------------------------------------------
* 
* @fn         bool XASN1::Decode(XBUFFER& databin)
* @brief      Decode
* @ingroup    XUTILS
* 
* @param[in]  databin : 
* 
* @return     bool : true if is succesful. 
* 
* --------------------------------------------------------------------------------------------------------------------*/
bool XASN1::Decode(XBUFFER& databin)
{
  if(!databin.GetSize()) return false;

  if(ber)
    {
      delete ber;
      ber = NULL;
    }

  ber = new XBER();
  if(!ber) return NULL;

  if(!ber->SetFromDump(databin))
    {
      delete ber;
      ber = NULL;      
    }

  return (ber?true:false);
}


/**-------------------------------------------------------------------------------------------------------------------
* 
* @fn         void XASN1::Clean()
* @brief      Clean the attributes of the class: Default initialice
* @note       INTERNAL
* @ingroup    XUTILS
* 
* @return     void : does not return anything. 
* 
* --------------------------------------------------------------------------------------------------------------------*/
void XASN1::Clean()
{
  ber = NULL;
}
